pragma solidity ^0.5.9;

import "openzeppelin-solidity/contracts/token/ERC20/ERC20.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Detailed.sol";
import "openzeppelin-solidity/contracts/token/ERC20/ERC20Burnable.sol";


// Class using ERC20, ERC20Burnable, ERC20Detailed for use on Cash36-Frontend to be independent from packaged contracts.
contract ERC20Token is ERC20, ERC20Burnable, ERC20Detailed {
    bool internal frontendOnly = true;
}
